/* styles */
import React, { useState } from "react";
import styles from './index.scss';


const titleColor = {
	width: "minContent",
	alignSelf: "center",
	marginLeft: "250px",
	marginTop: "3vh",
	gridRowStart: 1,
	fontFamily: "'Roboto', sansSerif",
	fontSize: "13vw",
	fontStyle: "normal",
	fontWeight: "bold",
	lineHeight: "90%",
	letterSpacing: "0.065em",
	background: "LinearGradient(to right, #000000 260px, #BAFFF7 260px, #BAFFF7 775px, #000000 775px)",
	WebKitTextFillColor: "transparent",
	WebkitBackgroundClip: "text",
}
const titleColor2 = {
	width: "minContent",
	alignSelf: "center",
	marginLeft: "250px",
	marginTop: "3vh",
	gridRowStart: 1,
	fontFamily: "'Roboto', sansSerif",
	fontSize: "13vw",
	fontStyle: "normal",
	fontWeight: "bold",
	lineHeight: "90%",
	letterSpacing: "0.065em",
	background: "linearGradient(to right, #000000 260px, #F9E2FF 260px, #F9E2FF 775px, #000000 775px)",
	WebkitTextFillColor: "transparent",
	WebkitBackgroundClip: "text",
}

const box = {
	position: "absolute",
	zIndex: "-2",
	backgroundColor: "#D1B5DB",
	width: "515px",
	height: "70vw",
	left: "510px",
	top: "0",
}

const box2 = {
	position: "absolute",
	zIndex: "-2",
	backgroundColor: "#36BAAA",
	width: "515px",
	height: "70vw",
	left: "510px",
	top: "0",
}

const style = {
    position: "absolute",
    top: "3vw",
    left: "90vw",
    borderRadius: "100%",
    width: "100px",
    height: "100px",
    fontFamily: "'Roboto', sansSerif",
    fontStyle: "normal",
    fontWeight: "bold",
    fontSize: "24px",
    lineHeight: "105.2%",
    letterSpacing: "0.065em",
    backgroundColor: "#D1B5DB",
    color: "#BAFFF7"
}

const style2 = {
    position: "absolute",
    top: "3vw",
    left: "90vw",
    borderRadius: "100%",
    width: "100px",
    height: "100px",
    fontFamily: "'Roboto', sansSerif",
    fontStyle: "normal",
    fontWeight: "bold",
    fontSize: "24px",
    lineHeight: "105.2%",
    letterSpacing: "0.065em",
    backgroundColor: "#36BAAA",
    color: "#F9E2FF"
}

const HeadlineModule = ({ title, subtitle, button }) => {
	const [color, setColor] = useState(true);

	function toggle() {
        setColor(!color);
    }

	return (
	<div className={styles.wrapper}>
		<div className={styles.box} style={!color ? box : box2} onClick={() => toggle()}></div>
		<h1 className={styles.title} data-content={title} style={!color ? titleColor : titleColor2} onClick={() => toggle()}>{title}</h1>
		<h2 className={styles.subtitle}>{subtitle}</h2>
		<button style={!color ? style : style2} onClick={() => toggle()}>{button}</button>
	</div>
	);
};

export default HeadlineModule;



