/* styles */
import React, {useState} from 'react';
import styles from './index.scss';

const titleColors = [
  {
    backgroundImage:
      'linear-gradient(to right, #000000 335px, #fffff 335px, #fffff 990px, #000000 990px)',
    ['-webkit-text-fill-color']: 'transparent',
    ['-webkit-background-clip']: 'text',
    backgroundColor: '#9ABCFF'
  },
  {
    background:
      'linear-gradient(to right, #000000 335px, #CD83DF 335px, #CD83DF 990px, #000000 990px)',
    WebkitTextFillColor: 'transparent',
    WebkitBackgroundClip: 'text'
  },
  {
    background:
      'linear-gradient(to right, #000000 335px, #FFD19A 335px, #FFD19A 990px, #000000 990px)',
    ['-webkit-text-fill-color']: 'transparent',
    ['-webkit-background-clip']: 'text'
  },
  {
    background:
      'linear-gradient(to right, #000000 335px, #FFF09F 335px, #FFF09F 990px, #000000 990px)',
    WebkitTextFillColor: 'transparent',
    WebkitBackgroundClip: 'text'
  },
  {
    background:
      'linear-gradient(to right, #000000 335px, #FFF09F 335px, #FFF09F 990px, #000000 990px)',
    ['-webkit-text-fill-color']: 'transparent',
    ['-webkit-background-clip']: 'text'
  },
  {
    background:
      'linear-gradient(to right, #000000 335px, #BAFFF7 335px, #BAFFF7 990px, #000000 990px)',
    WebkitTextFillColor: 'transparent',
    WebkitBackgroundClip: 'text'
  },
  {
    background:
      'linear-gradient(to right, #000000 335px, #F9E2FF 335px, #F9E2FF 990px, #000000 990px)',
    ['-webkit-text-fill-color']: 'transparent',
    ['-webkit-background-clip']: 'text'
  },
  {
    background:
      'linear-gradient(to right, #000000 335px, #FFD8F2 335px, #FFD8F2 990px, #000000 990px)',
    WebkitTextFillColor: 'transparent',
    WebkitBackgroundClip: 'text'
  },
  {
    background:
      'linear-gradient(to right, #000000 335px, #FF9A9A 335px, #FF9A9A 990px, #000000 990px)',
    ['-webkit-text-fill-color']: 'transparent',
    ['-webkit-background-clip']: 'text'
  }
];

const backgroundColors = [
  '#9ABCFF',
  '#FF9A9A',
  '#CD83DF',
  '#B5DBD4',
  '#DBB5CE',
  '#D1B5DB',
  '#36BAAA',
  '#9E77D0',
  '#9E77D0'
];

const boxColor = {
  backgroundColor: '#FF9A9A'
};

const boxColor2 = {
  backgroundColor: '#D1B5DB'
};

const buttonColor = {
  backgroundColor: '#FF9A9A',
  color: '#CD83DF'
};

const buttonColor2 = {
  backgroundColor: '#D1B5DB',
  color: '#BAFFF7'
};

const HeadlineModule = ({title, subtitle, button}) => {
  const [colorIndex, setColorIndex] = useState(0);

  const color = titleColors[colorIndex];

  function changeColor() {
    // if (colorIndex < titleColors.length - 1) {
    //   setColorIndex(colorIndex + 1);
    // } else {
    //   setColorIndex(0);
    // }
    setColorIndex((colorIndex + 1) % titleColors.length);
  }

  function changeBackgroundColor() {
    setColorIndex((backgroundColorIndex + 1) % titleColors.length);
  }

  return (
    <div className={styles.wrapper}>
      <div className={styles.box} style={color}></div>
      <h1 className={styles.title} style={color}>
        {title}
      </h1>
      <h2 className={styles.subtitle} style={color}>
        {subtitle}
      </h2>
      <button
        className={styles.button}
        style={color}
        onClick={() => changeColor()}
      >
        {button}
      </button>
    </div>
  );
};

export default HeadlineModule;
