/* styles */
import React, { useState } from "react";
import styles from './index.scss';


const titleColor = {
	width: "minContent",
	alignSelf: "center",
	marginLeft: "250px",
	marginTop: "3vh",
	gridRowStart: 1,
	fontFamily: "'Roboto', sansSerif",
	fontSize: "13vw",
	fontStyle: "normal",
	fontWeight: "bold",
	lineHeight: "90%",
	letterSpacing: "0.065em",
	background: "linear-gradient(to right, #000000 260px, #BAFFF7 260px, #BAFFF7 775px, #000000 775px)",
	WebkitTextFillColor: "transparent",
	WebkitBackgroundClip: "text",
}
const titleColor2 = {
	width: "minContent",
	alignSelf: "center",
	marginLeft: "250px",
	marginTop: "3vh",
	gridRowStart: 1,
	fontFamily: "'Roboto', sansSerif",
	fontSize: "13vw",
	fontStyle: "normal",
	fontWeight: "bold",
	lineHeight: "90%",
	letterSpacing: "0.065em",
	background: "linear-gradient(to right, #000000 260px, #CD83DF 260px, #CD83DF 775px, #000000 775px)",
	["-webkit-text-fill-color"]: "transparent",
	["-webkit-background-clip"]: "text",
}

const subtitle = {
	["align-self"]: "center",
	width: "50vw",
	["margin-left"]: "250px",
	["font-family"]: "'Roboto', sans-serif",
	["font-style"]: "normal",
	["font-weight"]: "bold",
	["line-height"]: "142.69%",
	["letter-spacing"]: "0.285em",
	["font-size"]: "3.1vw",
	["grid-row-start"]: 2,
	background: "linear-gradient(to right, #000000 255px, #BAFFF7 255px)",
	["-webkit-text-fill-color"]: "transparent",
	["-webkit-background-clip"]: "text",
}

const subtitle2 = {
	["align-self"]: "center",
	width: "50vw",
	["margin-left"]: "250px",
	["font-family"]: "'Roboto', sans-serif",
	["font-style"]: "normal",
	["font-weight"]: "bold",
	["line-height"]: "142.69%",
	["letter-spacing"]: "0.285em",
	["font-size"]: "3.1vw",
	["grid-row-start"]: 2,
	background: "linear-gradient(to right, #000000 255px, ##FF9A9A 255px)",
	["-webkit-text-fill-color"]: "transparent",
	["-webkit-background-clip"]: "text",
}

const box = {
	position: "absolute",
	zIndex: "-2",
	backgroundColor: "#D1B5DB",
	width: "515px",
	height: "70vw",
	left: "510px",
	top: "0",
}

const box2 = {
	position: "absolute",
	zIndex: "-2",
	backgroundColor: "#FF9A9A",
	color: "#CD83DF",
	width: "515px",
	height: "70vw",
	left: "510px",
	top: "0",
}

const style = {
    position: "absolute",
    top: "3vw",
    left: "90vw",
    borderRadius: "100%",
    width: "100px",
    height: "100px",
    fontFamily: "'Roboto', sansSerif",
    fontStyle: "normal",
    fontWeight: "bold",
    fontSize: "24px",
    lineHeight: "105.2%",
    letterSpacing: "0.065em",
    backgroundColor: "#D1B5DB",
    color: "#BAFFF7"
}

const style2 = {
    position: "absolute",
    top: "3vw",
    left: "90vw",
    borderRadius: "100%",
    width: "100px",
    height: "100px",
    fontFamily: "'Roboto', sansSerif",
    fontStyle: "normal",
    fontWeight: "bold",
    fontSize: "24px",
    lineHeight: "105.2%",
    letterSpacing: "0.065em",
    backgroundColor: "#FF9A9A",
    color: "#CD83DF"
}



const HeadlineModule = ({ title, subtitle, button }) => {
	const [color, setColor] = useState(true);

	function toggle() {
        setColor(!color);
    }

	return (
	<div className={styles.wrapper}>
		<div className={styles.box} style={!color ? box : box2} onClick={() => toggle()}></div>
		<h1 className={styles.title} data-content={title} style={!color ? titleColor : titleColor2} onClick={() => toggle()}>{title}</h1>
		<h2 className={styles.subtitle} style={!color ? subtitle : subtitle2} onClick={() => toggle()}>{subtitle}</h2>
		<button style={!color ? style : style2} onClick={() => toggle()}>{button}</button>
	</div>
	);
};

export default HeadlineModule;



