import React, { useState } from "react";
import "./ToggleButton.scss";




const ToggleButton = ({ title }) => {
    const [color, setColor] = useState(false);

    function toggle() {
        setColor(!color);
    }

    return (
        <button className={!color ? "toggleButton default-background" : "toggleButton onclick-background"} onClick={() => toggle()} >{title}</button>
    )
}

export default ToggleButton;
