/* styles */
import React, { useState } from "react";
import styles from './index.scss';

const box = {
	position: "absolute",
	zIndex: "-2",
	backgroundColor: "#D1B5DB",
	width: "515px",
	height: "70vw",
	left: "510px",
	top: "0",
}

const box2 = {
	position: "absolute",
	zIndex: "-2",
	backgroundColor: "red",
	color: "#F9E2FF",
	width: "515px",
	height: "70vw",
	left: "510px",
	top: "0",
}

const style = {
    position: "absolute",
    top: "3vw",
    left: "90vw",
    borderRadius: "100%",
    width: "100px",
    height: "100px",
    fontFamily: "'Roboto', sansSerif",
    fontStyle: "normal",
    fontWeight: "bold",
    fontSize: "24px",
    lineHeight: "105.2%",
    letterSpacing: "0.065em",
    backgroundColor: "#D1B5DB",
    color: "#BAFFF7"
}

const style2 = {
    position: "absolute",
    top: "3vw",
    left: "90vw",
    borderRadius: "100%",
    width: "100px",
    height: "100px",
    fontFamily: "'Roboto', sansSerif",
    fontStyle: "normal",
    fontWeight: "bold",
    fontSize: "24px",
    lineHeight: "105.2%",
    letterSpacing: "0.065em",
    backgroundColor: "#36BAAA",
    color: "#F9E2FF"
}

const HeadlineModule = ({ title, subtitle, button }) => {
	const [color, setColor] = useState(true);

	function toggle() {
        setColor(!color);
    }

	return (
	<div className={styles.wrapper}>
		<div className={styles.box} style={!color ? box : box2} onClick={() => toggle()}></div>
		<h1 className={styles.title} data-content={title}>{title}</h1>
		<h2 className={styles.subtitle}>{subtitle}</h2>
		<button style={!color ? style : style2} onClick={() => toggle()}>{button}</button>
	</div>
	);
};

export default HeadlineModule;



