import fetchUrl from '../fetchUrl';
import DefaultLayout from '../components/layouts/DefaultLayout';

import HeadlineModule from '../components/modules/HeadlineModule';
import Collage from '../components/modules/Collage/Collage.jsx';

import React, {useState} from 'react';
import styles from '../components/modules/HeadlineModule/index.scss';

const titleColors = [
  {
    background:
      'linear-gradient(to right, #000000 335px, #FFFFFF 335px, #FFFFFF 990px, #000000 990px)',
    ['-webkit-text-fill-color']: 'transparent',
    ['-webkit-background-clip']: 'text'
  },
  {
    background:
      'linear-gradient(to right, #000000 335px, #BBFFF2 335px, #BBFFF2 990px, #000000 990px)',
    WebkitTextFillColor: 'transparent',
    WebkitBackgroundClip: 'text'
  },
  {
    background:
      'linear-gradient(to right, #000000 335px, #FFFDCD 335px, #FFFDCD 990px, #000000 990px)',
    ['-webkit-text-fill-color']: 'transparent',
    ['-webkit-background-clip']: 'text'
  },
  {
    background:
      'linear-gradient(to right, #000000 335px, #FFD0EF 335px, #FFD0EF 990px, #000000 990px)',
    WebkitTextFillColor: 'transparent',
    WebkitBackgroundClip: 'text'
  },
  {
    background:
      'linear-gradient(to right, #000000 335px, #D3F5FF 335px, #D3F5FF 990px, #000000 990px)',
    ['-webkit-text-fill-color']: 'transparent',
    ['-webkit-background-clip']: 'text'
  },
  {
    background:
      'linear-gradient(to right, #000000 335px, #FFFFFF 335px, #FFFFFF 990px, #000000 990px)',
    WebkitTextFillColor: 'transparent',
    WebkitBackgroundClip: 'text'
  }
];

const backgroundColors = [
  {backgroundColor: '#9ABCFF', color: '#FFFFFF'},
  {backgroundColor: '#FFCBCB', color: '#BBFFF2'},
  {backgroundColor: '#FFD0EF', color: '#FFFDCD'},
  {backgroundColor: '#68DEC2', color: '#FFD0EF'},
  {backgroundColor: '#EABBFF', color: '#D3F5FF'},
  {backgroundColor: '#ECFFB6', color: '#FFFFFF'}
];

const IndexPage = ({data, button}) => {
  const [colorIndex, setColorIndex] = useState(0);
  const [backgroundColorIndex, setBackgroundColorIndex] = useState(0);

  const color = titleColors[colorIndex];
  const backgroundColor = backgroundColors[backgroundColorIndex];

  function changeColor() {
    // if (colorIndex < titleColors.length - 1) {
    //   setColorIndex(colorIndex + 1);
    // } else {
    //   setColorIndex(0);
    // }
    setColorIndex((colorIndex + 1) % titleColors.length);
  }

  function changeBackgroundColor() {
    setBackgroundColorIndex(
      (backgroundColorIndex + 1) % backgroundColors.length
    );
  }

  const {content} = data;
  const headlineModuleData = content.body.find(
    item => item.component === 'Headline'
  );
  const collageData = content.body.find(item => item.component === 'Collage');

  return (
    <DefaultLayout>
      {headlineModuleData ? (
        <HeadlineModule
          title={headlineModuleData.title}
          subtitle={headlineModuleData.subtitle}
          button={headlineModuleData.button}
          background={background}
          color={color}
        />
      ) : null}
      {collageData ? (
        <Collage
          title={collageData.title}
          project={collageData.project}
          project2={collageData.project2}
          project3={collageData.project3}
          project4={collageData.project4}
          project5={collageData.project5}
        />
      ) : null}
      <button
        className={styles.button}
        backgroundColor={backgroundColor}
        onClick={() => {
          changeColor();
          changeBackgroundColor();
        }}
      >
        {button}
      </button>
    </DefaultLayout>
  );
};

IndexPage.getInitialProps = async () => {
  const data = await fetchUrl(`http://localhost:3000/api/page/welcome`);

  return {data};
};

export default IndexPage;
