/* styles */
import React, {useState} from 'react';
import styles from './index.scss';

const colors = [
  {
    background:
      'linear-gradient(to right, #000000 335px, #fffff 335px, #fffff 990px, #000000 990px)',
    ['-webkit-text-fill-color']: 'transparent',
    ['-webkit-background-clip']: 'text'
  },
  {
    background:
      'linear-gradient(to right, #000000 335px, #CD83DF 335px, #CD83DF 990px, #000000 990px)',
    WebkitTextFillColor: 'transparent',
    WebkitBackgroundClip: 'text'
  },
  {
    background:
      'linear-gradient(to right, #000000 335px, #FFD19A 335px, #FFD19A 990px, #000000 990px)',
    ['-webkit-text-fill-color']: 'transparent',
    ['-webkit-background-clip']: 'text'
  },
  {
    background:
      'linear-gradient(to right, #000000 335px, #FFF09F 335px, #FFF09F 990px, #000000 990px)',
    WebkitTextFillColor: 'transparent',
    WebkitBackgroundClip: 'text'
  },
  {
    background:
      'linear-gradient(to right, #000000 335px, #FFF09F 335px, #FFF09F 990px, #000000 990px)',
    ['-webkit-text-fill-color']: 'transparent',
    ['-webkit-background-clip']: 'text'
  }
];

const titleColor = {
  background:
    'linear-gradient(to right, #000000 335px, #CD83DF 335px, #CD83DF 990px, #000000 990px)',
  WebkitTextFillColor: 'transparent',
  WebkitBackgroundClip: 'text'
};

const titleColor2 = {
  background:
    'linear-gradient(to right, #000000 335px, #BAFFF7 335px, #BAFFF7 990px, #000000 990px)',
  ['-webkit-text-fill-color']: 'transparent',
  ['-webkit-background-clip']: 'text'
};

const titleColor3 = {
  background:
    'linear-gradient(to right, #000000 335px, #fff 335px, #fff 990px, #000000 990px)',
  ['-webkit-text-fill-color']: 'transparent',
  ['-webkit-background-clip']: 'text'
};

const titleColor4 = {
  background:
    'linear-gradient(to right, #000000 335px, #00000 335px, #00000 990px, #000000 990px)',
  ['-webkit-text-fill-color']: 'transparent',
  ['-webkit-background-clip']: 'text'
};

const subtitleColor = {
  background:
    'linear-gradient(to right, #000000 335px, #CD83DF 335px, #CD83DF 990px, #000000 990px)',
  WebkitTextFillColor: 'transparent',
  WebkitBackgroundClip: 'text'
};

const subtitleColor2 = {
  background:
    'linear-gradient(to right, #000000 335px, #BAFFF7 335px, #BAFFF7 990px, #000000 990px)',
  ['-webkit-text-fill-color']: 'transparent',
  ['-webkit-background-clip']: 'text'
};

const boxColor = {
  backgroundColor: '#FF9A9A'
};

const boxColor2 = {
  backgroundColor: '#D1B5DB'
};

const buttonColor = {
  backgroundColor: '#FF9A9A',
  color: '#CD83DF'
};

const buttonColor2 = {
  backgroundColor: '#D1B5DB',
  color: '#BAFFF7'
};

const HeadlineModule = ({title, subtitle, button}) => {
  const [colorIndex, setColorIndex] = useState(0);
  const color = colors[colorIndex];
  // gör en for-loop istället
  function toggle() {
    if (colorIndex < colors.length - 1) {
      setColorIndex(colorIndex + 1);
    } else {
      setColorIndex(0);
    }

    // setColorIndex((colorIndex + 1) % colors.length)
  }
  console.log({colorIndex});

  return (
    <div className={styles.wrapper}>
      <div className={styles.box}></div>
      <h1 className={styles.title} style={color}>
        {title}
      </h1>
      <h2 className={styles.subtitle} style={color}>
        {subtitle}
      </h2>
      <button className={styles.button} style={color} onClick={() => toggle()}>
        {button}
      </button>
    </div>
  );
};

export default HeadlineModule;
